import React, { Fragment } from "react";
import styled from "styled-components";

const FanFormContent = ({ device, handleChange, getSensor }) => {
  console.log(typeof device.switch);
  const relatedSensors = getSensor(device.relation);
  return (
    <>
      <label>
        {" "}
        Select the sensor to listen:
        <select
          id="input_sensor"
          name="sensors"
          defaultValue={"none"}
          onChange={(ev) => handleChange(ev, "fan")}
        >
          <option value="none" disabled hidden>
            Select an Option
          </option>
          {relatedSensors.map((device) => (
            <option key={device.id} value={device.id}>
              {device.name}
            </option>
          ))}
        </select>
      </label>
      <label>
        Toggle OFF/ON:{" "}
        <Switch
          type="button"
          className={`switch ${device.switch && "active"}`}
          onClick={(ev) => handleChange(ev, "fan", true)}
          value={device.switch}
          title="Toggle me"
          id="input_switch"
        />
      </label>
    </>
  );
};

const Switch = styled.input`
  width: 0;
  overflow: hidden;
  padding-left: 68px;
  background: #c5c5c5 url() 2px center / auto 32px no-repeat;
  border-radius: 18px;
  border: 0;
  color: transparent;
  height: 36px;
  transition: all 0.3s ease-in-out;
  background-image: url("data:image/svg+xml;base64,");

  cursor: pointer;
  &.switch:disabled {
    cursor: default;
    background-image: url("data:image/svg+xml;base64,");
  }
  &.switch.active {
    background-color: #8ab82e;
    background-position: calc(100% - 2px) center;
  }
`;
export default FanFormContent;
